import { Link, useLocation } from 'react-router-dom'
import { useEffect, useState } from 'react'
import pokemonproject from '../Assets/pokemonproject.png'

export default function PokemonDetails() {
  const location = useLocation()
  const number = location.state.number
  const source = `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${number}.png`
  const [pokemon, setPokemon] = useState({})

  console.log(number, ' number')
  useEffect(() => {
    fetch(`https://pokeapi.co/api/v2/pokemon/${number}/`)
      .then((response) => response.json())
      .then((data) => {
        console.log(data, 'pokemon')
        setPokemon(data)
        document.getElementById('Sprite').src = source
      })
      .catch((err) => {
        console.log('Error encountered')
      })
  }, [])

  const toInitialMayusc = (str) => str.charAt(0).toUpperCase() + str.slice(1)

  return (
    <div className="App">
      <div className="LeftPanel" id="left">
        <img src={pokemonproject} className="Logo" alt="Pokemon Logo" />
        <br />
        <img src="" id="Sprite" />
      </div>
      <div className="RightPanel">
        <button className="GoBackBtn">Go back</button>
        <h4>Type</h4>
        <h4>Type: {}</h4>
        <div className="TechData">
          <span>Number: {number}</span>
          <span>Name: {pokemon.name}</span>
          <span>Height: {pokemon.height}</span>
          <span>Weight: {pokemon.weight}</span>
        </div>
        <div>
          Stats:
          <p>
            Hp:{' '}
            {pokemon?.stats.length ? pokemon?.stats[0]?.base_stat : 'not exist'}
          </p>
          <p>
            Attack:{' '}
            {pokemon?.stats.length ? pokemon?.stats[1]?.base_stat : 'not exist'}
          </p>
          <p>
            Defense:{' '}
            {pokemon?.stats.length ? pokemon?.stats[2]?.base_stat : 'not exist'}
          </p>
          <p>
            Special Attack:{' '}
            {pokemon?.stats.length ? pokemon?.stats[3]?.base_stat : 'not exist'}
          </p>
          <p>
            Special Defense:{' '}
            {pokemon?.stats.length ? pokemon?.stats[4]?.base_stat : 'not exist'}
          </p>
          <p>
            Speed:{' '}
            {pokemon?.stats.length ? pokemon?.stats[5]?.base_stat : 'not exist'}
          </p>
        </div>
      </div>
    </div>
  )
}
