import logo from './logo.svg'
import './App.css'
import { useEffect, useState } from 'react'
import pokemonproject from './Assets/pokemonproject.png'
import pokeball from './Assets/pokeballSprite.png'

function App() {
  let sprite
  const [pokemonList, setPokemonList] = useState([])

  useEffect(() => {
    fetch('https://pokeapi.co/api/v2/pokemon/?limit=20')
      .then((response) => response.json())
      .then((data) => {
        console.log(data.results)
        setPokemonList(data.results)
      })
      .catch((err) => {
        console.log(err.message)
      })
  }, [])

  useEffect(() => {
    fetch('https://pokeapi.co/api/v2/pokemon-form/1')
      .then((response) => response.json())
      .then((data) => {
        sprite = data.sprites.front_default

        document.getElementById('Sprite').src = sprite
      })
      .catch((err) => {
        console.log(err.message)
      })
  }, [])

  const updateSprite = (number) => {
    console.log(number)
  }

  return (
    <div className="App">
      <div className="LeftPanel" id="left">
        <img src={pokemonproject} className="Logo" alt="Pokemon Logo" />
        <br />
        <img src="" id="Sprite" />
      </div>
      <div className="RightPanel">
        {pokemonList?.map(function (pokemon) {
          return (
            <div
              className="pokemonNameDiv"
              onClick={updateSprite(pokemon.name)}
              key={pokemon.index}
            >
              {data.name.charAt(0).toUpperCase() + pokemon.name.slice(1)}
              <img src={pokeball} />
            </div>
          )
        })}
      </div>
    </div>
  )
}

export default App
