import logo from './logo.svg'
import './App.css'
import { useEffect, useState } from 'react'
import pokemonproject from './Assets/pokemonproject.png'
import pokeball from './Assets/pokeballSprite.png'

function App() {
  let sprite
  const [offset, setOffset] = useState(131)
  const [pokemonList, setPokemonList] = useState([])

  useEffect(() => {
    fetch('https://pokeapi.co/api/v2/pokemon/?limit=20')
      .then((response) => response.json())
      .then((data) => {
        console.log(data.results)
        setPokemonList(data.results)
      })
      .catch((err) => {
        console.log(err.message)
      })
  }, [])

  /*

  useEffect(() => {
    if (offset == 0) {
      //traer a bulbasaur
      fetch('https://pokeapi.co/api/v2/pokemon-form/bulbasaur')
        .then((response) => response.json())
        .then((data) => {
          sprite = data.sprites.front_default
          document.getElementById('Sprite').src = sprite
          setOffset(offset + 20)
          console.log('offset is ', offset)
        })
        .catch((err) => {
          console.log(err.message)
        })
    } else {
      //hacer el fetch apropiado
      fetch(`https://pokeapi.co/api/v2/pokemon-form/${offest}/`)
      //seleccionar el primer poke para la imagen
    }
  }, [])

  */

  useEffect(() => {
    fetch('https://pokeapi.co/api/v2/pokemon-form/bulbasaur')
      .then((response) => response.json())
      .then((data) => {
        sprite = data.sprites.front_default

        document.getElementById('Sprite').src = sprite
      })
      .catch((err) => {
        console.log(err.message)
      })
  }, [])

  function handleClick(index) {
    //console.log(name)
    console.log(index)
    let source = `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${index}.png`
    document.getElementById('Sprite').src = source
    //call image for pokemon name
    //const api = `https://pokeapi.co/api/v2/ability/${name}`
    //<img src="https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/1.png" id="Sprite">
  }

  return (
    <div className="App">
      <div className="LeftPanel" id="left">
        <img src={pokemonproject} className="Logo" alt="Pokemon Logo" />
        <br />
        <img src="" id="Sprite" />
      </div>
      <div className="RightPanel">
        {pokemonList?.map(function (pokemon) {
          console.log(pokemon)
          return (
            <div
              className="pokemonNameDiv"
              key={pokemon.index}
              onClick={() => handleClick(pokemon.index)}
            >
              {pokemon.name.charAt(0).toUpperCase() + pokemon.name.slice(1)}
              <img src={pokeball} />
            </div>
          )
        })}
        <button disabled={offset == 0}>Prev</button>
        <button disabled={offset >= 130}>Next</button>
      </div>
    </div>
  )
}

export default App
